def PatternCount(Text, Pattern):
    count = 0
    for i in range(len(Text) - len(Pattern) + 1):
        if Text[i:i+len(Pattern)] == Pattern:
            count += 1
    return count


# Example usage:
input_text = ""
pattern_to_count = "TAAACTTTA"

result = PatternCount(input_text, pattern_to_count)
print("Count:", result)
